#define LOWPREC lowp
// Uniforms look like they're shared between vertex and fragment shaders in GLSL, so we have to be careful to avoid name clashes

uniform sampler2D gm_BaseTexture;

uniform bool gm_PS_FogEnabled;
uniform vec4 gm_FogColour;
uniform bool gm_AlphaTestEnabled;
uniform float gm_AlphaRefValue;

void DoAlphaTest(vec4 SrcColour)
{
	if (gm_AlphaTestEnabled)
	{
		if (SrcColour.a <= gm_AlphaRefValue)
		{
			discard;
		}
	}
}

void DoFog(inout vec4 SrcColour, float fogval)
{
	if (gm_PS_FogEnabled)
	{
		SrcColour = mix(SrcColour, gm_FogColour, clamp(fogval, 0.0, 1.0)); 
	}
}

#define _YY_GLSLES_ 1
varying vec2 v_vTexcoord;
varying vec4 v_vColour;	
uniform float params[10];
vec2 size = vec2(params[0], params[1]);
#define res (size.xy/params[2])
float hardScan = params[3]; //-8.0;
float hardPix = params[4]; // -3.0;
float maskLight = params[5];

//---------------------- FUNCTIONS ----------------------------

// sRGB to Linear.
float ToLinear1(float c){
return(c <= 0.04045) ? c/12.92 : pow((c+0.055)/1.055,2.4);}

vec3 ToLinear(vec3 c){
return vec3(ToLinear1(c.r), ToLinear1(c.g), ToLinear1(c.b));}

// Linear to sRGB.
float ToSrgb1(float c){
return(c < 0.0031308 ? c * 12.92 : 1.055 * pow(c, 0.41666) - 0.055);}
vec3 ToSrgb(vec3 c){
return vec3(ToSrgb1(c.r), ToSrgb1(c.g), ToSrgb1(c.b));}

// Nearest emulated sample given floating point position and texel offset.
// Also zero's off screen.
vec3 Fetch(vec2 pos, vec2 off){
pos = floor(pos * res + off)/res;
if(max(abs(pos.x - 0.5), abs(pos.y - 0.5)) > 0.5) return vec3(0.0, 0.0, 0.0);
return ToLinear(texture2D(gm_BaseTexture, pos.xy).rgb);}

// Distance in emulated pixels to nearest texel.
vec2 Dist(vec2 pos){
pos = pos * res;
return -((pos - floor(pos)) - vec2(0.5));}
    
// 1D Gaussian.
float Gaus(float pos, float scale){
return exp2(scale * pos * pos);
}

// 3-tap Gaussian filter along horz line.
vec3 Horz3(vec2 pos, float off){
	vec3 b = Fetch(pos, vec2(-1.0, off));
	vec3 c = Fetch(pos, vec2( 0.0, off));
	vec3 d = Fetch(pos, vec2( 1.0, off));
	float dst = Dist(pos).x;
	// Convert distance to weight.
	float scale = hardPix;
	float wb = Gaus(dst - 1.0, scale);
	float wc = Gaus(dst + 0.0, scale);
	float wd = Gaus(dst + 1.0, scale);
	// Return filtered sample.
	return (b * wb + c * wc + d * wd)/(wb + wc + wd);}

// 5-tap Gaussian filter along horz line.
vec3 Horz5(vec2 pos, float off)
{
	vec3 a = Fetch(pos, vec2(-2.0, off));
	vec3 b = Fetch(pos, vec2(-1.0, off));
	vec3 c = Fetch(pos, vec2( 0.0, off));
	vec3 d = Fetch(pos, vec2( 1.0, off));
	vec3 e = Fetch(pos, vec2( 2.0, off));
	float dst = Dist(pos).x;
	// Convert distance to weight.
	float scale = hardPix;
	float wa = Gaus(dst - 2.0, scale);
	float wb = Gaus(dst - 1.0, scale);
	float wc = Gaus(dst + 0.0, scale);
	float wd = Gaus(dst + 1.0, scale);
	float we = Gaus(dst + 2.0, scale);
	// Return filtered sample.
	return (a * wa + b * wb + c * wc + d * wd + e * we)/(wa + wb + wc + wd + we);
}

// Return scanline weight.
float Scan(vec2 pos, float off)
{
	float dst = Dist(pos).y;
	return Gaus(dst + off, hardScan);
}

// Allow nearest three lines to effect pixel.
vec3 Tri(vec2 pos)
{
	vec3 a = Horz3(pos,  -1.0);
	vec3 b = Horz5(pos,   0.0);
	vec3 c = Horz3(pos,   1.0);
	float wa = Scan(pos, -1.0);
	float wb = Scan(pos,  0.0);
	float wc = Scan(pos,  1.0);
	
	return a * wa + b * wb + c * wc;
}

// Distortion of scanlines, and end of screen alpha.
vec2 Warp(vec2 pos)
{
	pos = pos * 2.0 - 1.0;    
	pos *= vec2(1.0 + (pos.y * pos.y) * 0.025, 1.0 + (pos.x * pos.x) * 0.025);
	return pos * 0.5 + 0.5;
}

// Shadow mask.
vec3 Mask(vec2 pos)
{
	pos.x += pos.y * 3.0;
	vec3 mask = vec3(.5, .5, .5);
	pos.x = fract(pos.x / 6.0);
	if (pos.x < 0.333) mask.r = maskLight;
	else if (pos.x < 0.666) mask.g = maskLight;
	else mask.b = maskLight;
	return mask;
}    

void main()
{
	vec4 fragColor = texture2D(gm_BaseTexture, v_vTexcoord );
	vec2 fragCoord = v_vTexcoord.xy * size.xy;
	vec2 pos = Warp(fragCoord.xy/size.xy);
	fragColor.rgb = Tri(pos) * Mask(fragCoord.xy);
	fragColor.a = 1.0;  
	fragColor.rgb = ToSrgb(fragColor.rgb);
	gl_FragColor = fragColor;
}

