	vec4 tempcol = blendednoisecol / ((g_Distort1Amount + g_Distort2Amount) * 0.5);	
	float colscale = dot(tempcol.rg, tempcol.rg);
	colscale *= 0.70711;
	colscale *= colscale;
	colscale *= colscale;	
	
