	wobbledcol.g = texture2D( gm_BaseTexture, v_vTexcoord + sampleoffset_g ).g;
	wobbledcol.b = texture2D( gm_BaseTexture, v_vTexcoord + sampleoffset_b ).b;
	wobbledcol.a = texture2D( gm_BaseTexture, v_vTexcoord + sampleoffset_r ).a;

